public class MainPage : Component
{
    private MauiControls.Entry _entryRef;

    public override VisualNode Render()
    {
        return new ContentPage
        {
            new VStack(spacing: 10)
            {
                new Entry(entryRef => _entryRef = entryRef),

                new Button("Click here!")
                    .OnClick(OnButtonClicked)
                    .VCenter()
                    .HCenter()
            }
        };
    }

    private void OnButtonClicked() => _entryRef?.Focus();
}

// Set dependency property
new Button().Set(SomeBindableProperty, someValue);

// Set attached property
new Button().SetAttachedProperty(SomeAttachedProperty, someValue);
