Example:
class CustomParameter { public int Numeric { get; set; } }

partial class ParametersPage : Component
{
    [Param] IParameter<CustomParameter> _customParameter;

    public override VisualNode Render() => new ContentPage
    {
        new VStack
        {
            new Button("Increment from parent", () => _customParameter.Set(_ => _.Numeric += 1)),
            new Label(_customParameter.Value.Numeric.ToString()),
            new ParameterChildComponent()
        }
        .Center()
    };
}

partial class ParameterChildComponent : Component
{
    [Param] IParameter<CustomParameter> _customParameter;

    public override VisualNode Render() => new VStack
    {
        new Button("Increment from child", () => _customParameter.Set(_ => _.Numeric++)),
        new Label(_customParameter.Value.Numeric.ToString())
    };
}

