partial class BusyComponent : Component
{
    [Prop] string _message;
    [Prop] bool _isBusy;

    public override VisualNode Render() => new StackLayout
    {
        new ActivityIndicator().IsRunning(_isBusy),
        new Label().Text(_message)
    };
}

class BusyPageState { public bool IsBusy { get; set; } }

class BusyPageComponent : Component<BusyPageState>
{
    protected override void OnMounted()
    {
        SetState(_ => _.IsBusy = true);
        Task.Run(async () => { await Task.Delay(3000); SetState(_ => _.IsBusy = false); });
        base.OnMounted();
    }

    public override VisualNode Render() => new ContentPage
    {
        State.IsBusy ? new BusyComponent().Message("Loading").IsBusy(true) : RenderPage()
    };

    private VisualNode RenderPage() => new Label("Done!").Center();
}
