public override VisualNode Render()
{
    return ContentPage(
        CollectionView()
            .ItemsSource(State.Persons, person =>
                VStack(spacing: 5,
                    Label($"{person.FirstName} {person.LastName}"),
                    Label(person.DateOfBirth.ToShortDateString()).FontSize(12).TextColor(Colors.Gray)
                ).Margin(5,10)
            )
    );
}
