Example:
class MainPageState
{
    public Animal[] Animals { get; set; }
}

class MainPage : Component<MainPageState>
{
    protected override void OnMounted()
    {
        State.Animals = new Animal[]
        {
            new Animal { Name = "American Black Bear", Location = "North America", ImageUrl = "https://upload.wikimedia.org/wikipedia/commons/0/08/01_Schwarzb%C3%A4r.jpg" },
            new Animal { Name = "Asian Black Bear", Location = "Asia", ImageUrl = "https://upload.wikimedia.org/wikipedia/commons/thumb/b/b7/Ursus_thibetanus_3_%28Wroclaw_zoo%29.JPG/180px-Ursus_thibetanus_3_%28Wroclaw_zoo%29.JPG" },
            new Animal { Name = "Brown Bear", Location = "Eurasia & North America", ImageUrl = "https://upload.wikimedia.org/wikipedia/commons/thumb/5/5d/Kamchatka_Brown_Bear_near_Dvuhyurtochnoe_on_2015-07-23.jpg/320px-Kamchatka_Brown_Bear_near_Dvuhyurtochnoe_on_2015-07-23.jpg" },
            new Animal { Name = "Grolar (Grizzly-Polar)", Location = "Canadian Arctic", ImageUrl = "https://upload.wikimedia.org/wikipedia/commons/thumb/7/7e/Grolar.JPG/276px-Grolar.JPG" },
        };
        base.OnMounted();
    }

    public override VisualNode Render()
    {
        return new ContentPage
        {
            new VStack(12)
            {
                new CarouselView()
                    .ItemsSource(State.Animals)
                    .ItemTemplate<Animal>(animal =>
                        new Image().Source(new UriImageSource { Uri = new Uri(animal.ImageUrl) })
                    )
                    .IndicatorView(
                        new IndicatorView()
                            .IndicatorColor(Colors.Red)
                            .IndicatorSize(12)
                            .IndicatorsShape(MauiControls.IndicatorShape.Circle)
                    )
            }
            .Padding(12)
        };
    }
}
