class RadioPageState
{
    public string Selected { get; set; } = "Radio 4";
}

class RadioPage : Component<RadioPageState>
{
    public override VisualNode Render()
        => new ContentPage
        {
            new VStack(spacing: 8)
            {
                new RadioButton("Radio 1")
                    .GroupName("choices")
                    .IsChecked(State.Selected == "Radio 1")
                    .OnCheckedChanged(_ => SetState(s => s.Selected = "Radio 1")),

                new RadioButton("Radio 2")
                    .GroupName("choices")
                    .IsChecked(State.Selected == "Radio 2")
                    .OnCheckedChanged(_ => SetState(s => s.Selected = "Radio 2")),

                new RadioButton("Radio 3")
                    .GroupName("choices")
                    .IsChecked(State.Selected == "Radio 3")
                    .OnCheckedChanged(_ => SetState(s => s.Selected = "Radio 3")),

                new RadioButton("Radio 4")
                    .GroupName("choices")
                    .IsChecked(State.Selected == "Radio 4")
                    .OnCheckedChanged(_ => SetState(s => s.Selected = "Radio 4")),

                new Label($"Selected: {State.Selected}")
            }
            .Padding(12)
        };
}
