Example:
// Shell: simple back button handler
new ContentPage()
    .OnBackButtonPressed(() => /* handle back */);

// NavigationPage: custom page override
public class CustomContentPage : MauiControls.ContentPage
{
    public event EventHandler BackButtonPressed;
    protected override bool OnBackButtonPressed()
    {
        BackButtonPressed?.Invoke(this, EventArgs.Empty);
        return base.OnBackButtonPressed();
    }
}

[Scaffold(typeof(MyNamespace.CustomContentPage))]
public partial class CustomContentPage { }

public class ChildPage : Component
{
    public override VisualNode Render()
        => new CustomContentPage()
        {
            new Button("Back")
                .VCenter()
                .HCenter()
                .OnClicked(() => Navigation.PopAsync())
        }
        .Set(MauiControls.NavigationPage.HasBackButtonProperty, true)
        .OnBackButtonPressed(() => /* intercept back */)
        .Title("Child Page");
}
