Example:
// Navigate to a child page
public class MainPageComponent : Component
{
    public override VisualNode Render()
    {
        return new NavigationPage()
        {
            new ContentPage()
            {
                new Button("Move To Page")
                    .VCenter()
                    .HCenter()
                    .OnClicked(OpenChildPage)
            }
            .Title("Main Page")
        };
    }

    private async void OpenChildPage()
    {
        await Navigation.PushAsync<ChildPageComponent>();
    }
}

// Child page with back navigation
public class ChildPageComponent : Component
{
    public override VisualNode Render()
    {
        return new ContentPage()
        {
            new Button("Back")
                .VCenter()
                .HCenter()
                .OnClicked(() => Navigation.PopAsync())
        }
        .Title("Child Page");
    }
}

// Passing props to the child page
private async void OpenChildPageWithProps()
{
    await Navigation.PushAsync<ChildPageComponent, ChildPageProps>(_ =>
    {
        _.InitialValue = 42;
        _.OnValueSet = newValue => SetState(s => s.Value = newValue);
    });
}
