Example:
record GreetingProps(string Name);

class Greeting : Component
{
    [Prop] public GreetingProps Props { get; set; }

    public override VisualNode Render()
        => new Label("Hello " + (Props?.Name ?? "world"));
}

class MainPage : Component
{
    public override VisualNode Render()
        => new ContentPage
        {
            new VStack(10)
            {
                new Greeting().Set(new GreetingProps("Maui")),
                new Greeting().Set(new GreetingProps("Reactor"))
            }
            .Center()
            .Padding(20)
        };
}
