Example:
// MVU equivalent of a login view
record LoginState(string Username, string Password);

class LoginPage : Component<LoginState>
{
    public override VisualNode Render()
        => new ContentPage
        {
            new VStack()
            {
                new Entry()
                    .Placeholder("Username")
                    .Text(State.Username)
                    .OnTextChanged(text => SetState(s => s.Username = text)),

                new Entry()
                    .Placeholder("Password")
                    .IsPassword(true)
                    .Text(State.Password)
                    .OnTextChanged(text => SetState(s => s.Password = text)),

                new Button("Login")
                    .OnClicked(() => DoLogin(State.Username, State.Password))
            }
            .Center()
        };

    void DoLogin(string u, string p) { /* ... */ }
}
