Example:
# Install template
dotnet new install Reactor.Maui.TemplatePack

# Create new project
dotnet new maui-reactor-startup -o my-new-project
cd .\my-new-project\

# Build/run Android
dotnet build -f net9.0-android
dotnet build -t:Run -f net9.0-android

# Install hot-reload console
dotnet tool install -g Reactor.Maui.HotReload
# Or update
dotnet tool update -g Reactor.Maui.HotReload

# Add feature switches to csproj (Debug)
<ItemGroup Condition="'$(Configuration)'=='Debug'">
  <RuntimeHostConfigurationOption Include="MauiReactor.HotReload" Value="true" Trim="false" />
</ItemGroup>

# Start hot reload (Simple)
dotnet-maui-reactor -f net9.0-android
# Start hot reload (Full)
dotnet-maui-reactor -f net9.0-android --mode Full

# Migrate existing app: Program.cs
public static MauiApp CreateMauiApp()
{
    var builder = MauiApp.CreateBuilder();
    builder
        .UseMauiReactorApp<MainPage>()
        .ConfigureFonts(fonts =>
        {
            fonts.AddFont("OpenSans-Regular.ttf", "OpenSansRegular");
            fonts.AddFont("OpenSans-SemiBold.ttf", "OpenSansSemiBold");
        });
    return builder.Build();
}

// MainPage component
class MainPage : Component
{
    public override VisualNode Render()
        => new ContentPage
        {
            new Label("Hi!").Center()
        };
}
