// State toggles opacity/scale; WithAnimation animates transitions
class MainPageState { public bool Toggle { get; set; } }

class MainPage : Component<MainPageState>
{
    public override VisualNode Render()
    {
        return new ContentPage
        {
            new Image()
                .HCenter()
                .VCenter()
                .Source("city.jpg")
                .OnTap(()=> SetState(s => s.Toggle = !s.Toggle))
                .Opacity(State.Toggle ? 1.0 : 0.0)
                .Scale(State.Toggle ? 1.0 : 0.5)
                .Margin(10)
                .WithAnimation(TimeSpan.FromMilliseconds(600), Easing.CubicOut)
        };
    }
}

