class CounterPageState { public int Counter { get; set; } public bool TimerRunning { get; set; } }

class CounterPage : Component<CounterPageState>
{
    public override VisualNode Render()
        => new ContentPage
        {
            new VStack
            {
                new Timer()
                    .IsEnabled(State.TimerRunning)
                    .Interval(1000)
                    .OnTick(() => SetState(s => s.Counter++)),

                new Label($"Counter: {State.Counter}"),

                new Button(State.TimerRunning ? "Pause" : "Run")
                    .OnClicked(() => SetState(s => s.TimerRunning = !s.TimerRunning))
            }
            .Spacing(10)
            .Center()
        };
}
